from unittest import TestCase
from genie.libs.sdk.apis.iosxe.ie3k.platform.execute import execute_copy_noverify
from unittest.mock import Mock


class TestExecuteCopyNoverify(TestCase):

    def test_execute_copy_noverify(self):
        self.device = Mock()
        results_map = {
            'copy /noverify flash:ie9k_iosxe.17.09.02.SPA.bin sdflash:ie9k_iosxe.17.09.02.SPA.bin': '''Destination filename [ie9k_iosxe.17.09.02.SPA.bin]? 
%Warning:There is a file already existing with this name 
Do you want to over write? [confirm]
Copy in progress...
588746269 bytes copied in 42.996 secs (13693047 bytes/sec)'''
        }
        
        def results_side_effect(arg, **kwargs):
            return results_map.get(arg)
        
        self.device.execute.side_effect = results_side_effect
        
        result = execute_copy_noverify(self.device, 'flash', 'sdflash', 600, 'ie9k_iosxe.17.09.02.SPA.bin')
        self.assertIn(
            'copy /noverify flash:ie9k_iosxe.17.09.02.SPA.bin sdflash:ie9k_iosxe.17.09.02.SPA.bin',
            self.device.execute.call_args_list[0][0]
        )
        expected_output = None
        self.assertEqual(result, expected_output)
